import React from 'react';
import DayPicker from 'react-day-picker';
import 'react-day-picker/lib/style.css';
import data from './data.json';

const birthdays = {
  3.3: ['Mirko', 'Gianni'],
  8: ['Elena'],
  9: ['Irene'],
  12: ['Paolo', 'Giorgia'],
  18: ['Claudia'],
  22: ['Maria', 'Luigi'],
  25: ['Simone'],
  26: ['Marta'],
};
function renderDay(day) {
  const date = day.getDate();
  const dateStyle = {
    position: 'absolute',
    color: 'black',
    bottom: 0,
    right: 0,
    fontSize: 20,
  };
  const birthdayStyle = { fontSize: '0.8em', textAlign: 'left' };
  const cellStyle = {
    height: 50,
    width: 60,
    position: 'relative',
  };
  return (
    <div style={cellStyle}>
      <div style={dateStyle}>{date}</div>
      {birthdays[date] &&
        birthdays[date].map((name, i) => (
          <div key={i} style={birthdayStyle}>
            {name}
          </div>
        ))}
    </div>
  );
}

export default function Example() {
  return (
    <DayPicker
      initialMonth={new Date(2020, 4)}
      selectedDays={[
        new Date(data[0].year, data[0].month, data[0].day),
        new Date(data[1].year, data[1].month, data[1].day),
        new Date(data[2].year, data[2].month, data[2].day),
      ]}
      renderDay={renderDay}

    />
  );
}
